import React from 'react';
import Tile from './Tile';
import spring from "./image.png";

import './style.css';

class TraineeWork extends React.Component {
    render(){
        springDesc = "Java and Spring were used to develop different web-apps, including doctor appointment apps, online testing apps, and to-do apps.  The trainees learned how to manage java beans and configure a Spring project using annotations, XML, and classes.  The trainees also learned how to connect Spring projects to MySQL databases, use dependency injection, and how to use the Spring Web Services."
        return (
            <main className="trainee_work_content">
                <div className="title">Trainee Work</div>
                <Tile alt="Spring" image={spring} desc={springDesc}/>
            </main>
        )
    }
}

export default TraineeWork;
